import {useEffect, useState } from 'react';
import res from '../../services/res';
import { Link} from 'react-router-dom';

function Home(){

    const [filmes, setFilmes] = useState([]);

    useEffect(() => {
        async function loadFilmes(){
            const response = await res.get("movie/now_playing", {
                params:{
                    api_key: "0cac2d596b64554674c2786c5ac18d09",
                    language: "pt-BR",
                    page: 1,
                }
            })
            console.log(response.data.results);
            setFilmes(response.data.results);
        }
        loadFilmes();
    },[]);

    return(
        <div  className='container-home'>
            <div className='lista-filmes-em-cartaz'>
                {filmes.map((filme) => {
                    return(
                        <article key={filme.id}>
                            <h2>{filme.title}</h2>
                            <img src={`https://image.tmdb.org/t/p/original/${filme.poster_path}`} alt={filme.title}></img>
                            <Link to={`/filme/${filme.id}`}>Acessar</Link>
                        </article>
                    )
                })}
            </div>
        </div>
    )
}

export default Home;
