import {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import './favoritos.css';

function Favoritos(){

    const [filme, setFilme] = useState([]);

    useEffect(() => {
        const minhaLista = localStorage.getItem("appfilmes");
        setFilme(JSON.parse(minhaLista) || []);

    },[])

    return(
        <div className='meus-filmes'>
            {filme.map((filme) => {
                return(
                    <li key={filme.id}><h2 className='meus-filmes-title'>{filme.title}</h2>
                        <div>
                            <Link className='meus-filmes-btn' to={`/filme/${filme.id}`}>Ver detalhes</Link>
                            <button className='meus-filmes-btn'>Excluir</button>
                        </div>
                    </li>
                )
            })}
        </div>
    )
}

export default Favoritos;
