import {useEffect, useState} from 'react';
import res from '../../services/res';

function MelhoresAvaliados(){
    const [filmes, setFilmes] = useState([]);

    useEffect(() => {
        async function loadFilmes(){
            const response = await res.get("/movie/top_rated", {
                params:{
                    api_key: "0cac2d596b64554674c2786c5ac18d09",
                    language: "pt-BR",
                    page: 1,
                }
            })
            console.log(response.data);
            setFilmes(response.data.results);
        }
        loadFilmes();
    },[]);
    return(
        <div  className='container-home'>
        <h1 className='titulo-pagina'>FILMES EM CARTAZ</h1>
        <div className='lista-filmes-em-cartaz'>
            {filmes.map((filme) => {
                return(
                    <article key={filme.id} className='lista-filmes-em-cartaz-filme'>
                        <h2 className='lista-filmes-em-cartaz-filme-titulo'>{filme.title}</h2>
                        <img className='lista-filmes-em-cartaz-filme-img' src={`https://image.tmdb.org/t/p/original/${filme.poster_path}`} alt={filme.title}></img>
                        <Link className='lista-filmes-em-cartaz-filme-btn' to={`/filme/${filme.id}`}>Acessar</Link>
                    </article>
                )
            })}
        </div>
    </div>
    )
}

export default  MelhoresAvaliados;
